import { Component } from '@angular/core';
import { APP_TITLE } from '../../../constants';

@Component({
  selector: 'app-footer',
  templateUrl: './footer.component.html',
  styleUrls: ['./footer.component.css']
})
export class FooterComponent {
  currentYear = new Date().getFullYear();
  appTitle = APP_TITLE;
}

<footer class="bg-slate-800 py-6 text-center shadow-top mt-auto">
  <div class="container mx-auto px-4">
    <p class="text-sm text-slate-400">
      &copy; {{ currentYear }} {{ appTitle }}. All rights reserved (simulated).
    </p>
    <p class="text-xs text-slate-500 mt-1">
      Disclaimer: This tool is for illustrative and educational purposes only and does not constitute financial advice. (Angular Version)
    </p>
  </div>
</footer>
