import { Component } from '@angular/core';
import { APP_TITLE } from '../../../constants';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {
  appTitle = APP_TITLE;
}

<header class="bg-slate-800 shadow-lg sticky top-0 z-50">
  <div class="container mx-auto px-4 py-4 flex items-center justify-between">
    <div class="flex items-center space-x-3">
      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-10 h-10 text-sky-400">
        <path stroke-linecap="round" stroke-linejoin="round" d="M2.25 18 9 11.25l4.306 4.306a11.95 11.95 0 0 1 5.814-5.518l2.74-1.22m0 0-5.94-2.281m5.94 2.28-2.28 5.941" />
      </svg>
      <h1 class="text-2xl font-bold text-sky-400">{{ appTitle }}</h1>
    </div>
    <p class="text-sm text-slate-400 hidden md:block">Get AI-driven insights for your investments (Angular Version)</p>
  </div>
</header>
