import { Component, EventEmitter, Input, Output, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, FormArray, Validators } from '@angular/forms';
import { PortfolioItem } from '../../../types';

@Component({
  selector: 'app-portfolio-input-form',
  templateUrl: './portfolio-input-form.component.html',
  styleUrls: ['./portfolio-input-form.component.css']
})
export class PortfolioInputFormComponent implements OnInit {
  @Input() isLoading: boolean = false;
  @Output() portfolioSubmit = new EventEmitter<PortfolioItem[]>();

  portfolioForm: FormGroup;

  constructor(private fb: FormBuilder) {
    this.portfolioForm = this.fb.group({
      items: this.fb.array([])
    });
  }

  ngOnInit(): void {
    // Add some default items for demonstration
    this.addDefaultItems();
  }

  get items(): FormArray {
    return this.portfolioForm.get('items') as FormArray;
  }

  createItem(): FormGroup {
    return this.fb.group({
      id: [Date.now().toString() + Math.random().toString(36).substring(2)], // Unique ID
      name: ['', Validators.required],
      symbol: ['', Validators.required],
      allocation: [0, [Validators.required, Validators.min(0), Validators.max(100)]]
    });
  }

  addDefaultItems() {
    const defaultPortfolio: PortfolioItem[] = [
      { id: Date.now().toString(), name: 'Apple Inc.', symbol: 'AAPL', allocation: 30 },
      { id: (Date.now() + 1).toString(), name: 'Vanguard S&P 500 ETF', symbol: 'VOO', allocation: 40 },
      { id: (Date.now() + 2).toString(), name: 'iShares MSCI EAFE ETF', symbol: 'EFA', allocation: 20 },
      { id: (Date.now() + 3).toString(), name: 'Bitcoin Trust', symbol: 'GBTC', allocation: 10 },
    ];
    defaultPortfolio.forEach(item => {
      this.items.push(this.fb.group({
        id: [item.id],
        name: [item.name, Validators.required],
        symbol: [item.symbol, Validators.required],
        allocation: [item.allocation, [Validators.required, Validators.min(0), Validators.max(100)]]
      }));
    });
  }

  addItem(): void {
    this.items.push(this.createItem());
  }

  removeItem(index: number): void {
    this.items.removeAt(index);
  }

  onSubmit(): void {
    if (this.portfolioForm.valid) {
      this.portfolioSubmit.emit(this.portfolioForm.value.items as PortfolioItem[]);
    } else {
      // Mark fields as touched to show validation errors
      this.portfolioForm.markAllAsTouched();
      console.error("Form is invalid", this.portfolioForm.value);
    }
  }

  get totalAllocation(): number {
    return this.items.controls.reduce((sum, control) => {
      const allocation = control.get('allocation')?.value || 0;
      return sum + parseFloat(allocation);
    }, 0);
  }
}

<form [formGroup]="portfolioForm" (ngSubmit)="onSubmit()" class="space-y-6 bg-slate-800 p-6 rounded-lg shadow-xl">
  <div>
    <h2 class="text-xl font-semibold text-sky-400 mb-4">Your Portfolio (Angular)</h2>
    <div formArrayName="items">
      <div *ngFor="let item of items.controls; let i = index" [formGroupName]="i" class="grid grid-cols-1 md:grid-cols-12 gap-3 mb-3 p-3 bg-slate-700/50 rounded-md">
        <div class="md:col-span-4">
          <label [for]="'name-' + i" class="block text-sm font-medium text-slate-300 mb-1">Name</label>
          <input type="text" [id]="'name-' + i" formControlName="name"
                 class="w-full bg-slate-700 text-slate-100 border-slate-600 rounded-md shadow-sm p-2 focus:ring-sky-500 focus:border-sky-500"
                 placeholder="e.g., Apple Inc." [readOnly]="isLoading">
          <!-- Basic validation message example -->
          <div *ngIf="items.controls[i].get('name')?.invalid && (items.controls[i].get('name')?.dirty || items.controls[i].get('name')?.touched)" class="text-red-400 text-xs mt-1">Name is required.</div>
        </div>
        <div class="md:col-span-3">
          <label [for]="'symbol-' + i" class="block text-sm font-medium text-slate-300 mb-1">Symbol</label>
          <input type="text" [id]="'symbol-' + i" formControlName="symbol" (input)="items.controls[i].get('symbol')?.setValue($event.target.value.toUpperCase())"
                 class="w-full bg-slate-700 text-slate-100 border-slate-600 rounded-md shadow-sm p-2 focus:ring-sky-500 focus:border-sky-500"
                 placeholder="e.g., AAPL" [readOnly]="isLoading">
           <div *ngIf="items.controls[i].get('symbol')?.invalid && (items.controls[i].get('symbol')?.dirty || items.controls[i].get('symbol')?.touched)" class="text-red-400 text-xs mt-1">Symbol is required.</div>
        </div>
        <div class="md:col-span-3">
          <label [for]="'allocation-' + i" class="block text-sm font-medium text-slate-300 mb-1">Alloc. %</label>
          <input type="number" [id]="'allocation-' + i" formControlName="allocation"
                 class="w-full bg-slate-700 text-slate-100 border-slate-600 rounded-md shadow-sm p-2 focus:ring-sky-500 focus:border-sky-500"
                 placeholder="e.g., 25" min="0" max="100" step="0.01" [readOnly]="isLoading">
          <div *ngIf="items.controls[i].get('allocation')?.invalid && (items.controls[i].get('allocation')?.dirty || items.controls[i].get('allocation')?.touched)" class="text-red-400 text-xs mt-1">
            <span *ngIf="items.controls[i].get('allocation')?.errors?.['required']">Allocation is required.</span>
            <span *ngIf="items.controls[i].get('allocation')?.errors?.['min'] || items.controls[i].get('allocation')?.errors?.['max']">Must be 0-100.</span>
          </div>
        </div>
        <div class="md:col-span-2 flex items-end">
          <button type="button" (click)="removeItem(i)" [disabled]="isLoading"
                  class="w-full text-red-400 hover:text-red-300 p-2 rounded-md hover:bg-red-700/50 disabled:opacity-50 transition-colors duration-150"
                  aria-label="Remove asset">
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-5 h-5 mx-auto"><path stroke-linecap="round" stroke-linejoin="round" d="m14.74 9-.346 9m-4.788 0L9.26 9m9.968-3.21c.342.052.682.107 1.022.166m-1.022-.165L18.16 19.673a2.25 2.25 0 0 1-2.244 2.077H8.084a2.25 2.25 0 0 1-2.244-2.077L4.772 5.79m14.456 0a48.108 48.108 0 0 0-3.478-.397m-12.56 0c1.153 0 2.24.032 3.287.082m0 0V4.875c0-.621.504-1.125 1.125-1.125h2.25c.621 0 1.125.504 1.125 1.125v.918m-3.287-.082H8.482M9.75 5.79v-.918a1.125 1.125 0 0 1 1.125-1.125h2.25A1.125 1.125 0 0 1 14.25 4.87v.918m0 0c-.328.047-.656.087-.985.119m3.452-.119c-2.589-.072-5.243-.111-7.941-.111m-2.04 0a48.108 48.108 0 0 1 3.478-.397m12.56 0c-1.153 0-2.24.032-3.287.082m0 0V4.875c0-.621.504-1.125 1.125-1.125h2.25c.621 0 1.125.504 1.125 1.125v.918m-3.287-.082H8.482m0 0c.328.047.656.087.985.119m3.452-.119c2.589-.072 5.243-.111 7.941-.111S5.08 5.63 2.5 5.707m-2.04 0a48.108 48.108 0 0 0 3.478.397" /></svg>
          </button>
        </div>
      </div>
    </div>
  </div>

  <div class="border-t border-slate-700 pt-4">
    <button type="button" (click)="addItem()" [disabled]="isLoading"
            class="flex items-center justify-center w-full px-4 py-2 border border-sky-500 text-sky-400 rounded-md shadow-sm hover:bg-sky-700/30 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-slate-800 focus:ring-sky-500 disabled:opacity-50 transition-colors duration-150 mb-4">
      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-5 h-5 mr-2"><path stroke-linecap="round" stroke-linejoin="round" d="M12 4.5v15m7.5-7.5h-15" /></svg>
      Add Asset
    </button>
  </div>

  <div class="flex justify-between items-center mt-2 mb-4 p-3 bg-slate-700/30 rounded-md">
      <span class="text-sm font-medium text-slate-300">Total Allocation:</span>
      <span class="text-lg font-semibold" [ngClass]="{'text-green-400': totalAllocation === 100, 'text-yellow-400': totalAllocation !== 100 && totalAllocation > 0, 'text-red-400': totalAllocation > 100 || totalAllocation <=0 && items.length > 0 }">
        {{ totalAllocation | number:'1.0-2' }}%
      </span>
  </div>
   <div *ngIf="totalAllocation !== 100 && items.length > 0" class="text-yellow-400 text-xs text-center mb-3">
        Total allocation should ideally be 100%.
   </div>


  <button type="submit" [disabled]="isLoading || portfolioForm.invalid || totalAllocation === 0"
          class="w-full bg-sky-600 hover:bg-sky-500 text-white font-semibold py-3 px-4 rounded-md shadow-md focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-slate-800 focus:ring-sky-500 disabled:opacity-50 disabled:cursor-not-allowed transition-colors duration-150">
    <span *ngIf="!isLoading">Analyze Portfolio</span>
    <span *ngIf="isLoading">Analyzing...
      <svg class="inline w-4 h-4 ml-2 animate-spin text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
        <circle class="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" stroke-width="4"></circle>
        <path class="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
      </svg>
    </span>
  </button>
</form>
